		poor_wysiwyg_editor.panel = document.createElement('div');
		poor_wysiwyg_editor.editor = document.createElement('div');
		poor_wysiwyg_editor.container = document.createElement('div');
		this.container.setAttribute('id', 'poor_wysiwyg_container');
		this.container.setAttribute('style', 'position: relative; width: ' + width +';');
		this.editor.contentEditable = true;
		this.editor.setAttribute('style', 'padding: 5px; border: 1px solid black; min-height: ' + height + ';');
		this.panel.setAttribute('style', '' + fixed);
		this.editor.setAttribute('id', 'poor_wysiwyg_editor');
		this.panel.setAttribute('id', 'poor_wysiwyg_toolbar');
		this.editor.addEventListener("input", this.SetVaule);
		this.editor.addEventListener("paste", this.OnPaste);
		let parent = this.textarea.parentNode;
		this.toolbar(params.toolbar);
		this.container.appendChild(this.panel);
		this.container.appendChild(this.editor);
		parent.insertBefore(this.container, this.textarea);
		if(poor_wysiwyg_editor.saveText)
			poor_wysiwyg_editor.editor.innerHTML = localStorage.getItem('poor_wysiwyg_editor');
